from subprocess import check_outputfrom ._common import scpuinfovirtual_cmd = 'uname -M'def cpu_info():    cmd = 'prtconf'    try:        output = check_output(cmd)        output_dict = {}        for line in output.split('\n'):            line_split = line.split(':')            if len(line_split) < 2:                continue            output_dict[line_split[0].strip().lower()] = line_split[1].strip()        return scpuinfo(            brand=output_dict.get('processor type', ''),            frequency=output_dict.get('processor clock speed', ''),            family='',            vendor_id=output_dict.get('processor version', ''),            stepping='',            cache_size='',            model='')    except:        return scpuinfo(* ['' for _ in xrange(7)])